mapper.select:
select MAKUP.id, MAKUP.xh, MAKUP.xm, MAKUP.kch, MAKUP.kcm, MAKUP.xf, MAKUP.max_score, MAKUP.if_make_up, MAKUP.if_pay, CODE_ZXJXJHXNXQXX.zxjxjhm as ZXJXJHM from MAKUP LEFT JOIN CODE_ZXJXJHXNXQXX ON CODE_ZXJXJHXNXQXX.ZXJXJHH = MAKUP.JXJHH
and CODE_ZXJXJHXNXQXX.zxjxjhh = #{zxjxjhh}
and MAKUP.xh = #{xh}

select MAKUP.id, MAKUP.xh, MAKUP.xm, MAKUP.kch, MAKUP.kcm, MAKUP.xf, MAKUP.max_score, MAKUP.if_make_up, MAKUP.if_pay, CODE_ZXJXJHXNXQXX.zxjxjhm as ZXJXJHM from MAKUP LEFT JOIN CODE_ZXJXJHXNXQXX ON CODE_ZXJXJHXNXQXX.ZXJXJHH = MAKUP.JXJHH
MAKUP.if_make_up = 1
and CODE_ZXJXJHXNXQXX.zxjxjhh = #{zxjxjhh}
and MAKUP.xh like concat('%',concat(#{xh},'%'))
and MAKUP.xm like concat('%',concat(#{xm},'%'))
and MAKUP.kch like concat('%',concat(#{kch},'%'))
and MAKUP.kcm like concat('%',concat(#{kcm},'%'))
and MAKUP.xf = #{xf}
and MAKUP.max_score = #{maxScore}

mapper.update:
update MAKUP makeup set if_make_up = #{makeupFlag}
where id in

	#{id}

update MAKUP makeup set if_pay = #{payFlag}
where id in

	#{id}
